                day: 'Feb 5, 2022',
                reminder: 'true',
            },
            {
                id: 2,
                text: 'to do 2',
                day: 'Feb 5, 2022',
                reminder: 'true',
            },
            {
                id: 3,
                text: 'to do 3',
                day: 'Feb 5, 2022',
                reminder: 'false',
            },
        ]
)

  return (
    <div>Tasks</div>
  )
}

export default Tasks
