import Header from './components/Header';
import Tasks from './components/Tasks';
import { useState } from 'react'

// const name = 'Lavi'
// const x=true
// function title*() {
//   return(
//     <h5>Hi React:)</h5>
//   )
// }  
// Comment  
const App = () => {

  const [tasks, setTasks] = useState([
    {
        id: 1,
        text: 'to do 1',
        day: 'Feb 5, 2022',
        reminder: 'true',
    },
    {
        id: 2,
        text: 'to do 2',
        day: 'Feb 5, 2022',
        reminder: 'true',
    },
    {
        id: 3,
        text: 'to do 3',
        day: 'Feb 5, 2022',
        reminder: 'false',
    },
])


const deleteTask = (id) => {
  setTasks(tasks.filter((task) => task.id !== id))
  console.log('deleted: ', id)
}

  return (
    <div className="container">

      <Header />   
      {/* <Header title='Hello...' />  overrides the defaultProps set in Header component*/}
      {/* <h1>Hello {name}</h1>
    <p>{x ? 'Yes': 'No'}</p> */}
    <p>&nbsp;&nbsp;{tasks.length > 0 ? <Tasks tasks={tasks} onDelete={deleteTask} /> : 'No tasks added yet'}</p>
    </div>
  );
}

export default App;
